import React,{useState} from 'react';
import Todo from "./components/Todo";
import Form from "./components/Form";
import FilterButton from "./components/FilterButton";
import {nanoid} from 'nanoid';

function App(props) {

  const filterList = props.filters.map(filter =><FilterButton aria = {filter.aria} text={filter.text} />);
  const [tasks,setTasks] = useState(props.tasks)

  function addTask(name) {
    const newTask = { id: "todo-"+nanoid(), name: name, completed: false };
    setTasks([...tasks, newTask]);
  }
  function toggleTaskCompleted(id){
    const updatedTasks = tasks.map(task=> {
      if (id === task.id){
        return {...task, completed : !task.completed} ;
      }
      return task;
    });
    setTasks(updatedTasks);
    console.log(tasks[0]);
  }
  function deletingTask(id){
    const deleteTask = tasks.filter(task => id!==task.id);
    setTasks(deletingTask);

  }

  const taskList = tasks.map(task => <Todo name={task.name} id={task.id} completed={task.completed} key={task.id} toggleTaskCompleted={toggleTaskCompleted} deletingTask={deletingTask}/>);
  const taskNoun = taskList.length !== 1 ? 'tasks':'task';
  const headingText = `${taskList.length} ${taskNoun} remaining`;
   return (
    <div className="todoapp stack-large">
      <h1>TodoMatic</h1>
      <Form addTask={addTask}/>

      <div className="filters btn-group stack-exception">
       {filterList}
      </div>
      <h2 id="list-heading">
        {headingText}
      </h2>
      <ul
        role="list"
        className="todo-list stack-large stack-exception"
        aria-labelledby="list-heading"
      >
       {taskList}

      </ul>
    </div>
  );
}

export default App;
